mapper.select:
SELECT DATASTRING FROM ${unit.data_table_name}
where
unit_num = #{unit_num} and
INSERTTIME = #{inserttime}
limit 1;

SELECT DFOO.UNIT_NUM ,DFOO.DATASTRING ,DFOO.XHQD ,DFOO.SJ ,DFOO.GATEWAYFRONTINDEX ,DFOO.SERVERINDEX ,DFOO.MININDEX ,DFOO.INSERTTIME FROM UNIT_DIS UNIT LEFT JOIN ${unit.data_table_name} DFOO ON DFOO.UNIT_NUM = UNIT.UNIT_NUM
DFOO.INSERTTIME BETWEEN #{t1} AND #{t2}
DFOO.INSERTTIME > #{t1}
DFOO.INSERTTIME < #{t2}
order by inserttime desc limit #{searchstart},#{onepagecount}

mapper.delete:
delete from ${unit.data_table_name}

    UNIT_NUM = #{unit.unit_num} AND
    INSERTTIME IN

        #{date}
